const body = $input.first().json.body;
const action = body.action;

// Get existing watch list from static data
const staticData = $getWorkflowStaticData('global');
let watchList = staticData.watchList || [];

if (action === 'add') {
  // Check if URL already exists for this guild
  const exists = watchList.some(p => p.url === body.url && p.guild_id === body.guild_id);

  if (exists) {
    return [{
      json: {
        success: false,
        action: 'add',
        message: 'Product already in watch list: ' + body.url
      }
    }];
  }

  // Add new product
  const newProduct = {
    url: body.url,
    product_name: body.product_name || 'Unknown Product',
    guild_id: body.guild_id,
    added_by: body.added_by,
    interval_minutes: body.interval_minutes || 5,
    logs_channel_id: body.logs_channel_id,
    alerts_channel_id: body.alerts_channel_id,
    added_at: new Date().toISOString(),
    last_checked: null,
    last_in_stock: false
  };

  watchList.push(newProduct);
  staticData.watchList = watchList;

  return [{
    json: {
      success: true,
      action: 'added',
      message: 'Added to watch list: ' + body.url
    }
  }];

} else if (action === 'remove') {
  const initialLength = watchList.length;
  watchList = watchList.filter(p => !(p.url === body.url && p.guild_id === body.guild_id));
  staticData.watchList = watchList;

  if (watchList.length < initialLength) {
    return [{
      json: {
        success: true,
        action: 'removed',
        message: 'Removed from watch list: ' + body.url
      }
    }];
  } else {
    return [{
      json: {
        success: false,
        action: 'remove',
        message: 'Product not found in watch list: ' + body.url
      }
    }];
  }

} else if (action === 'list') {
  const guildProducts = watchList.filter(p => p.guild_id === body.guild_id);

  if (guildProducts.length === 0) {
    return [{
      json: {
        success: true,
        action: 'list',
        products: [],
        message: 'No products in watch list'
      }
    }];
  }

  const productList = guildProducts.map((p, i) => 
    `${i + 1}. **${p.product_name}** (every ${p.interval_minutes}m)\n   ${p.url}`
  ).join('\n');

  return [{
    json: {
      success: true,
      action: 'list',
      products: guildProducts,
      count: guildProducts.length,
      message: `**Watch List (${guildProducts.length} products):**\n${productList}`
    }
  }];
}

return [{ json: { success: false, message: 'Unknown action: ' + action } }];

// Get watch list from static data
const staticData = $getWorkflowStaticData('global');
let watchList = staticData.watchList || [];

const now = new Date();
const productsToCheck = [];

for (let i = 0; i < watchList.length; i++) {
  const product = watchList[i];
  if (!product.url) continue;

  const intervalMinutes = parseInt(product.interval_minutes) || 5;
  const lastChecked = product.last_checked ? new Date(product.last_checked) : null;

  let shouldCheck = false;
  if (!lastChecked) {
    shouldCheck = true;
  } else {
    const minutesSinceLastCheck = (now - lastChecked) / (1000 * 60);
    shouldCheck = minutesSinceLastCheck >= intervalMinutes;
  }

  if (shouldCheck) {
    productsToCheck.push({
      json: {
        ...product,
        index: i
      }
    });
  }
}

if (productsToCheck.length === 0) {
  return [{ json: { skip: true, message: 'No products due for checking' } }];
}

return productsToCheck;

const input = $input.first().json;
const productData = $('Get Products to Check').first().json;

// Get HTML from response
const html = input.data || input.body || (typeof input === 'string' ? input : '');

if (!html || typeof html !== 'string') {
  return [{
    json: {
      ...productData,
      success: false,
      error: 'Could not fetch product page'
    }
  }];
}

// Extract product name from page if not set
let productName = productData.product_name;
if (!productName || productName === 'Unknown Product') {
  const ogTitleMatch = html.match(/property="og:title"\s+content="([^"]+)"/i);
  const titleMatch = html.match(/<title>([^<]+)<\/title>/i);
  if (ogTitleMatch) {
    productName = ogTitleMatch[1].replace(' â€“ Ubiquiti Store USA', '').trim();
  } else if (titleMatch) {
    productName = titleMatch[1].replace(' â€“ Ubiquiti Store USA', '').trim();
  }
}

// Check stock status
const schemaInStock = html.includes('"availability":"https://schema.org/InStock"');
const schemaOutOfStock = html.includes('"availability":"https://schema.org/OutOfStock"');
const hasAddToCart = html.includes('>Add to Cart<');
const hasSoldOut = html.includes('>Sold Out<');

// Extract price
let price = 'Unknown';
const priceMatch = html.match(/"price":(\d+(?:\.\d+)?)/); 
const currencyMatch = html.match(/"priceCurrency":"([A-Z]+)"/);
if (priceMatch) {
  const amount = parseFloat(priceMatch[1]);
  const currency = currencyMatch ? currencyMatch[1] : 'USD';
  price = currency === 'USD' ? `$${amount.toFixed(2)}` : `${amount.toFixed(2)} ${currency}`;
}

const inStock = schemaInStock || (hasAddToCart && !schemaOutOfStock);
const wasInStock = productData.last_in_stock === true || productData.last_in_stock === 'true';
const sendAlert = inStock && !wasInStock;

return [{
  json: {
    ...productData,
    success: true,
    product_name: productName,
    inStock: inStock,
    wasInStock: wasInStock,
    sendAlert: sendAlert,
    price: price,
    checkedAt: new Date().toISOString()
  }
}];

const result = $input.first().json;

// Update the watch list in static data
const staticData = $getWorkflowStaticData('global');
let watchList = staticData.watchList || [];

if (result.index !== undefined && watchList[result.index]) {
  watchList[result.index].last_checked = result.checkedAt;
  watchList[result.index].last_in_stock = result.inStock;
  watchList[result.index].product_name = result.product_name;
  staticData.watchList = watchList;
}

return [$input.first()];
